","keccak256":"0xa1e8e83cd0087785df04ac79fb395d9f3684caeaf973d9e2c71caef723a3a5d6","license":"MIT"},"contracts/AsterBadge.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.11;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "./interfaces/IAsterBadge.sol";

contract AsterBadge is Ownable, AccessControl, ERC721Enumerable, IAsterBadge {
    mapping(address => mapping(uint256 => uint256)) public userMintedCount; // user -> campaign id -> amount
    mapping(uint256 => uint256) public tokenIdToCId; // token id -> campaign id
    mapping(uint256 => uint256) public cIdMintedCount;
    mapping(uint256 => uint256) public cIdBurntCount;

    bytes32 public constant MINTER_ROLE =
        0x9f2df0fed2c77648de5860a4cc508cd0818c85b8b8a1ab4ceeef8d981c8956a6;

    string public baseURI;

    bool public transferable = true;

    uint256 public tokenIds;

    error NonTransferable();

    constructor(
        string memory _name,
        string memory _symbol
    ) ERC721(_name, _symbol) {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function mint(
        address _to,
        uint256 _cId
    ) external onlyRole(MINTER_ROLE) returns (uint256) {
        uint256 tokenId = ++tokenIds;

        tokenIdToCId[tokenId] = _cId;
        cIdMintedCount[_cId]++;
        userMintedCount[_to][_cId]++;

        _safeMint(_to, tokenId);
        return tokenId;
    }

    function mintBatch(
        address _to,
        uint256 _amount,
        uint256 _cId
    ) external onlyRole(MINTER_ROLE) returns (uint256[] memory) {
        uint256[] memory ids = new uint256[](_amount);
        for (uint256 i; i < _amount; ++i) {
            ids[i] = ++tokenIds;

            tokenIdToCId[ids[i]] = _cId;
            cIdMintedCount[_cId]++;
            userMintedCount[_to][_cId]++;

            _safeMint(_to, ids[i]);
        }
        return ids;
    }

    function burn(uint256 _tokenId) external onlyRole(MINTER_ROLE) {
        require(
            _isApprovedOrOwner(_msgSender(), _tokenId),
            "ERC721: caller is not approved or owner"
        );
        _burn(_tokenId);

        uint256 cId = tokenIdToCId[_tokenId];
        cIdBurntCount[cId]++;
        delete tokenIdToCId[_tokenId];
    }

    function burnBatch(uint256[] calldata _ids) external onlyRole(MINTER_ROLE) {
        for (uint256 i; i < _ids.length; ++i) {
            require(
                _isApprovedOrOwner(_msgSender(), _ids[i]),
                "ERC721: caller is not token owner or approved"
            );
            _burn(_ids[i]);

            uint256 cId = tokenIdToCId[_ids[i]];
            cIdBurntCount[cId]++;
            delete tokenIdToCId[_ids[i]];
        }
    }

    // override transfer
    function transferFrom(
        address _from,
        address _to,
        uint256 _tokenId
    ) public virtual override {
        if (!transferable) {
            revert NonTransferable();
        }
        super.transferFrom(_from, _to, _tokenId);
    }

    function safeTransferFrom(
        address _from,
        address _to,
        uint256 _tokenId
    ) public virtual override {
        if (!transferable) {
            revert NonTransferable();
        }
        super.safeTransferFrom(_from, _to, _tokenId);
    }

    function safeTransferFrom(
        address _from,
        address _to,
        uint256 _tokenId,
        bytes memory _data
    ) public virtual override {
        if (!transferable) {
            revert NonTransferable();
        }
        super.safeTransferFrom(_from, _to, _tokenId, _data);
    }

    function campaignSupply(uint256 _cId) public view returns (uint256) {
        return cIdMintedCount[_cId] - cIdBurntCount[_cId];
    }

    function supportsInterface(
        bytes4 _interfaceId
    )
        public
        view
        virtual
        override(AccessControl, ERC721Enumerable)
        returns (bool)
    {
        return
            _interfaceId == type(IAsterBadge).interfaceId ||
            super.supportsInterface(_interfaceId);
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    function setBaseURI(string memory _uri) external onlyOwner {
        baseURI = _uri;
    }

    function setTransferable(bool _transferable) external onlyOwner {
        transferable = _transferable;
    }
}
","keccak256":"0xb839bb20124b6a2b781ef4d9fd19f1ce9aa7866054eb25c2729698a334962342","license":"MIT"},"contracts/interfaces/IAsterBadge.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.11;

interface IAsterBadge {
    function mint(address _to, uint256 _cId) external returns (uint256);

    function mintBatch(
        address _to,
        uint256 _amount,
        uint256 _cId
    ) external returns (uint256[] memory);

    function burn(uint256 _tokenId) external;

    function burnBatch(uint256[] calldata _ids) external;
}
