pragma solidity 0.8.11;

import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "./interfaces/IAsterBadge.sol";

contract AsterStation is Ownable, EIP712 {
    using ECDSA for bytes32;

    mapping(bytes32 => bool) public hasMinted; // check reuse signId

    address public adminSigner;

    bytes32 public constant NFTCLAIM_TYPEHASH =
        keccak256("NFT(uint256 cId,address nft,bytes32 signId,address mintTo)");

    bytes32 public constant NFTCLAIMBATCH_TYPEHASH =
        keccak256(
            "NFT(uint256 cId,address nft,bytes32[] signIds,address mintTo)"
        );

    error AlreadyMinted();
    error InvalidSignature();

    event Claim(
        uint256 cId,
        uint256 nftId,
        bytes32 signId,
        address indexed nft,
        address mintTo
    );

    event ClaimBatch(
        uint256 cId,
        uint256[] nftIds,
        bytes32[] signIds,
        address indexed nft,
        address mintTo
    );

    constructor(
        string memory _name,
        string memory _version,
        address _signer
    ) EIP712(_name, _version) {
        adminSigner = _signer;
    }

    function claim(
        uint256 _cId,
        IAsterBadge _nft,
        bytes32 _signId,
        address _mintTo,
        bytes calldata _signature
    ) external {
        if (hasMinted[_signId]) {
            revert AlreadyMinted();
        }

        if (!verifyClaim(_cId, _nft, _signId, _mintTo, _signature)) {
            revert InvalidSignature();
        }

        hasMinted[_signId] = true;
        uint256 nftId = _nft.mint(_mintTo, _cId);

        emit Claim(_cId, nftId, _signId, address(_nft), _mintTo);
    }

    function claimBatch(
        uint256 _cId,
        IAsterBadge _nft,
        bytes32[] calldata _signIds,
        address _mintTo,
        bytes calldata _signature
    ) external {
        uint256 amount = _signIds.length;
        for (uint256 i; i < amount; ++i) {
            bytes32 signId = _signIds[i];
            if (hasMinted[signId]) {
                revert AlreadyMinted();
            }
            hasMinted[signId] = true;
        }

        if (!verifyClaimBatch(_cId, _nft, _signIds, _mintTo, _signature)) {
            revert InvalidSignature();
        }

        uint256[] memory nftIds = _nft.mintBatch(_mintTo, amount, _cId);

        emit ClaimBatch(_cId, nftIds, _signIds, address(_nft), _mintTo);
    }

    function verifyClaim(
        uint256 _cId,
        IAsterBadge _nft,
        bytes32 _signId,
        address _mintTo,
        bytes calldata _signature
    ) public view returns (bool) {
        return
            _hashTypedDataV4(
                keccak256(
                    abi.encode(NFTCLAIM_TYPEHASH, _cId, _nft, _signId, _mintTo)
                )
            ).recover(_signature) == adminSigner;
    }

    function verifyClaimBatch(
        uint256 _cId,
        IAsterBadge _nft,
        bytes32[] calldata _signIds,
        address _mintTo,
        bytes calldata _signature
    ) public view returns (bool) {
        return
            _hashTypedDataV4(
                keccak256(
                    abi.encode(
                        NFTCLAIMBATCH_TYPEHASH,
                        _cId,
                        _nft,
                        keccak256(abi.encodePacked(_signIds)),
                        _mintTo
                    )
                )
            ).recover(_signature) == adminSigner;
    }

    function updateAdminSigner(address _signer) external onlyOwner {
        adminSigner = _signer;
    }
}
